mapper.select:
select *
from user_todolist
where task_userid = #{task_userid}
  and task_type = #{task_type}

mapper.update:
update user_todolist
set task_type=#{task_type},
    task_finishtime=#{finishTime}
where task_userid = #{task_userid}
  and task_id = #{taskId}

mapper.select:
select *
from user_todolist
where task_userid = #{task_userid}
  and task_id = #{taskId}

mapper.update:
update user_todolist
set task_content=#{task_content}
where task_userid = #{task_userid}
  and task_id = #{taskId}

mapper.delete:
delete
from user_todolist
where task_userid = #{task_userid}
  and task_id = #{taskId}
